using System.Web;

namespace Common
{
    public static partial class GlobalUtilities
    {
        /// <summary>
        /// Gets the full product image path of the provided product image.
        /// </summary>
        /// <param name="productImage">The name of the product image</param>
        /// <returns>The absolute Uri of the provided product image</returns>
        public static string GetProductImagePath(string productImage)
        {
            productImage = productImage ?? string.Empty;
            if (productImage.IsNullOrEmpty() || productImage.Contains("nopic.gif"))
            {
                return "data:image/png;base64,";
            }
            else
            {
                var scheme = (HttpContext.Current != null) ? HttpContext.Current.Request.Url.Scheme : "http:";
                if (productImage.Contains("http:") || productImage.Contains("https:"))
                {
                    if (scheme == "https" && productImage.Contains("http:"))
                    {
                        productImage = productImage.Replace("http:", "https:");
                    }
                    return productImage;
                }
                else
                {
                    return "{0}://api.exigo.com/4.0/{1}/productimages/{2}".FormatWith(scheme, GlobalSettings.Exigo.Api.CompanyKey, productImage);
                }
                //var scheme = HttpContext.Current.Request.Url.Scheme;
                //if (productImage.Contains("http:") || productImage.Contains("https:"))
                //{
                //    if (scheme == "https" && productImage.Contains("http:"))
                //    {
                //        productImage = productImage.Replace("http:", "https:");
                //    }
                //    return productImage;
                //}
                //else
                //{
                //    return "{0}://api.exigo.com/4.0/{1}/productimages/{2}".FormatWith(scheme, GlobalSettings.Exigo.Api.CompanyKey, productImage);
                //}
            }
        }
    }
}